import numpy as np

class Policy:
    def __init__(self):
        self.memory = {'key_picked_up': False, 'door_opened': False}
        self.notes = []

    def act(self, observation):
        if not self.memory['key_picked_up']:
            if observation['image'][3][5][0] == 5:  # If there is a key in front of the agent
                self.memory['key_picked_up'] = True
                self.notes.append('Picked up the key')
                return 3  # Pick up the key
            else:
                return 2  # Move forward to find the key
        elif not self.memory['door_opened']:
            if observation['image'][3][5][0] == 4 and observation['image'][3][5][2] == 2:  # If there is a locked door in front of the agent
                if 5 in observation['image'][3][5]:  # If the agent has the key
                    self.memory['door_opened'] = True
                    self.notes.append('Opened the door')
                    return 5  # Use the key to open the door
                else:
                    if observation['direction'] == 0:
                        return 0  # Turn left
                    else:
                        return 2  # Move forward to find the key
            else:
                return 2  # Move forward to find the door
        else:
            if observation['image'][3][5][0] == 7:  # If there is a box in front of the agent
                return 3  # Pick up the box
            else:
                return 2  # Move forward to find the box

    def update(self, observation, action, reward, next_observation):
        # No updates needed for this simple policy
        pass

    def produce_report(self) -> str:
        return f"Policy performance summary: Key picked up - {self.memory['key_picked_up']}, Door opened - {self.memory['door_opened']}"
